function (inputs) {
  const defaultPlan = "Basic"
  return {
    annotation: {
      "kloudlite.io/billing-plan": defaultPlan,
      "kloudlite.io/billable-quantity": inputs.cpu,
      "kloudlite.io/is-shared": true,
    },
    inputs: {
      ...inputs,
      cpu: `${inputs.cpu * 1000}m`,
      size: `${inputs.size}Gi`,
    },
  };
}
